import React, { useContext, useState } from 'react'
import { GlobalStoreContext } from '../store'

function SongCard(props) {
    const { store } = useContext(GlobalStoreContext);

    const { song, index } = props;
    let cardClass = "list-card unselected-list-card";

    let dragfrom = ""
    let dragto = ""

    function handleDeleteSong() {
        store.showDeleteSongModal(index);
    }

    function handleDoubleClick() {
        store.showEditSongModal(index);
    }

    function handleDragStart(event) {
        dragfrom = event.target.id

        event.dataTransfer.setData("song", event.target.id)
        // Started dragging
        const draggingsong = song
        console.log("dragging", dragfrom)
    }

    function handleDragEnd(event) {
        // Dropped dragging

        console.log("from", dragfrom)
        console.log("to", dragto);
    }

    function handleDragOver(event) {
        dragfrom = event.target.id

        console.log("song moused over to", event.target.id)
    }

    function handleDragLeave(event) {
        dragfrom = dragto
        console.log("leaving song")
    }

    // function handleMoveSong(event) {
    //     // console.log("event", event.target)
    //     // console.log("song", event.dataTransfer)
    //     console.log("droppin")
    // }

    return (
        <div
            key={index}
            id={'song-' + index + '-card'}
            className={cardClass}
            onDoubleClick={handleDoubleClick}
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            draggable={true}
        >
            {index + 1}.{" "}
            <a
                id={'song-' + index + '-link'}
                className="song-link"
                href={"https://www.youtube.com/watch?v=" + song.youTubeId}>
                {song.title} by {song.artist}
            </a>
            <input
                type="button"
                id={"remove-song-" + index}
                className="list-card-button"
                onClick={handleDeleteSong}
                value={"\u2715"}
            />
        </div>
    );
}

export default SongCard;
