import { useContext } from 'react'
import { GlobalStoreContext} from '../store'

function EditSongModal() {
    const { store } = useContext(GlobalStoreContext);

    let title = ""
    let artist = ""
    let youtubeId = ""
    if (store.currentSongIndex != null){
        title = store.currentSongTitle
        artist = store.currentSongArtist
        youtubeId = store.currentList.songs[store.currentSongIndex].youTubeId
    }

    function updateSongContent() {
        console.log(document.getElementById("songtitle").value)
    }

    return (
        <div className="modal" id="edit-song-modal" data-animation="slideInOutLeft">
            <div className="modal-dialog">
                <div className="edit-dialog-header">
                    Title<input type="text" id="songtitle" value={title} onChange={updateSongContent}></input>
                    Artist<input type="text" id="songartist" value={artist}></input>
                    YouTube ID<input type="text" id="youtubeID" value={youtubeId}></input>
                </div>
                <div className="confirm-cancel-container">
                    <input type="button" id="edit-confirm" className="confirm-cancel-button" value="Confirm"></input>
                    <input type="button" id="edit-cancel" className="confirm-cancel-button" value="Cancel"></input>
                </div>
            </div>
        </div>
    );
}

export default EditSongModal;
