import { useContext } from 'react'
import { GlobalStoreContext} from '../store'

function DeleteListModal() {
    const { store } = useContext(GlobalStoreContext);
    // const history = useHistory();

    // const name = ""
    // if (store.currentList) {
    //     name = store.currentList.name;
    // }

    function confirmDeleteList() {
        store.callDeleteList();
    }

    function hideDeleteListModal() {
        store.hideDeleteListModal();
    }

    return (
        <div class="modal" id="delete-list-modal" data-animation="slideInOutLeft">
            Do you wish to permanently delete the {console.log("playlist",store.playlistToDelete)} playlist?
            <input type="button" id="delete-confirm" onClick={confirmDeleteList} value="Confirm"></input>
            <input type="button" id="delete-cancel" onClick={hideDeleteListModal} value="Cancel"></input>
        </div>
    );
}

export default DeleteListModal;
